package assets.minecessity.blocks;import assets.minecessity.EntityLightBullet;import net.minecraft.block.Block;import net.minecraft.block.material.Material;import net.minecraft.creativetab.CreativeTabs;import net.minecraft.entity.player.EntityPlayer;import net.minecraft.world.World;import java.util.Random;public class BlockParticle extends Block {	public BlockParticle() {		super(Material.ground);        setTickRandomly(true);        setCreativeTab(CreativeTabs.tabRedstone);	}	@Override	public int tickRate(World world) {		return 1;	}	@Override	public void onNeighborBlockChange(World world, int i, int j, int k, Block l) {		if (powered(world, i, j, k))			shootParticles(world, i, j, k);	}	@Override	public void updateTick(World world, int i, int j, int k, Random random) {		if (powered(world, i, j, k))			shootParticles(world, i, j, k);	}	public boolean powered(World world, int i, int j, int k) {		return world.getBlockPowerInput(i, j, k) != 0 || world.isBlockIndirectlyGettingPowered(i, j, k);	}	public void shootParticles(World world, int i, int j, int k) {		if (!world.isRemote) {			EntityLightBullet bullet = new EntityLightBullet(world, world.getBlockMetadata(i, j, k));			bullet.setPosition(i + 0.5, j + 1, k + 0.5);            Random random = new Random();            bullet.motionX = random.nextFloat()/4-1/8;            bullet.motionY = random.nextFloat()*3+1;            bullet.motionZ = random.nextFloat()/4-1/8;			world.spawnEntityInWorld(bullet);		}	}	@Override	public boolean onBlockActivated(World world, int i, int j, int k, EntityPlayer entityplayer, int par6, float par7, float par8, float par9) {		int meta = world.getBlockMetadata(i, j, k);		if (meta < 3 && meta >= 0)			meta++;		else			meta = 0;		world.setBlockMetadataWithNotify(i, j, k, meta, 3);		world.markBlockForUpdate(i, j, k);        world.playSoundEffect((double)i + 0.5D, (double)j + 0.5D, (double)k + 0.5D, "random.click", 1.0F, world.rand.nextFloat() * 0.1F + 0.9F);		return true;	}}